{
//Here we pull data from rset
}
}/*, "Optional Error message "*/);

DB.select("SELECT column1, column2 FROM table WHERE column3=?", new ParamReadStH(){
@Override
public void setParams(PreparedStatement stmt) throws SQLException
{
//If you use params in query we HAVE to set them here
stmt.setInt(1, 100);
}
@Override
public void handleRead(ResultSet rset) throws SQLException
{
//Here we can pull data from rset

}
}/*, "Optional Error message "*/);

DB.insertUpdate("UPDATE table SET column1=?, column2=? WHERE column3=?", new IUStH(){
@Override
public void handleInsertUpdate(PreparedStatement stmt) throws SQLException
{
//if we wanna batch processing we may iterate some collection adding to batch and then executing whole batch
//for(Element e: collection)
//{
stmt.setInt(1, 20);
stmt.setInt(2, 0); // values will ofc depend on e ;)
stmt.addBatch();
//}
stmt.executeBatch(); // exec batch
}
}/*, "Optional Error message "*/);

DB.insertUpdate("UPDATE table SET column1=?, column2=? WHERE column3=?", new IUStH(){
@Override
public void handleInsertUpdate(PreparedStatement stmt) throws SQLException
{
//Here we can set parameters of stmt if used ? in query
stmt.setInt(1, 20);
